import { createStore } from 'vuex'
import axios from 'axios';

const getData = (route) => {
  try {
    return axios({
      method: 'get',
      url: route,
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI4YTc1MjhjNjdlOWNhZmJiMWJjMzk2ZjUxYjE3ZmIwZiIsInN1YiI6IjY0NzFmY2IxZGQ3MzFiMDExNzhmMmM2MSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.0epWf0vNKEK1xjkaZUj_OKt2MFSySz-omr8o2Z9Hoso'
      }
    }).then((response) => {
    return response.data;
  });
  } catch (error) {
    console.log(error);
  }
}

export default createStore({
  state: {
    searchResults: null
  },
  getters: {
  },
  mutations: {
    SetMovieSearchResults (state, results) {
      state.searchResults = results.data;
    }
  },
  actions: {
    getLatestMovies() {
      return getData('https://api.themoviedb.org/3/movie/now_playing');
    },
    getMovieSearchResults(query) {
      return getData(`https://api.themoviedb.org/3/search/movie?query=${query}`);
    },
    getTVSearchResults(query) {
      return getData(`https://api.themoviedb.org/3/search/tv?query=${query}`);
    }
  },
  modules: {
  }
})
